mapper.select:
SELECT parkno, memberno, name, phone, address, area, price, cmt, file1, r FROM ( SELECT parkno, memberno, name, phone, address, area, price, cmt, file1, rownum as r FROM ( SELECT parkno, memberno, name, phone, address, area, price, cmt, file1 FROM park
WHERE (address LIKE '%' || #{address} || '%' OR name LIKE '%' || #{address} || '%')
) ) WHERE r >= #{start_num} AND r <= #{end_num}
